import React, { useState } from "react";
import AddUserForm from "./AddUserForm";
import DisplayUsers from "./DisplayUsers";
import { firebase } from "../firebase";

function Hero({ handleLogout }) {
  const initialItemState = [{ id: null, name: "", weight: "" }];
  const [showModal, setShowModal] = useState(false);
  const [currentItem, setCurrentItem] = useState(initialItemState);
  const editItem = (item) => {
    setCurrentItem({
      id: item.id,
      name: item.name,
      weight: item.weight,
    });
  };
  const updateItem = ({ currentItem }, updatedItem) => {
    console.log(
      "It send the item to the updated item function:",
      updatedItem,
      currentItem.id
    );
    firebase
      .firestore()
      .collection("items")
      .doc(currentItem.id)
      .update(updatedItem);
  };

  return (
    <>
      <nav className="flex justify-between w-full h-16 items-center bg-blue-600 shadow-lg">
        <h1 className="font-semibold text-xl pl-3">Weight Tracker App</h1>
        <button
          className="h-9 w-20 font-semibold bg-white mr-3 rounded-full hover:bg-gray-50"
          onClick={handleLogout}
        >
          Logout
        </button>
      </nav>
      <AddUserForm />
      <DisplayUsers showModal={showModal} setShowModal={setShowModal} />
    </>
  );
}

export default Hero;
