import React, { useEffect, useState } from "react";
import { firebase } from "../firebase";
import { MdDelete } from "react-icons/md";
import { BiPencil } from "react-icons/bi";
import Modal from "./Modal";

function useWeights() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const unsubscribe = firebase
      .firestore()
      .collection("users-weight")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshot) => {
        const newUsers = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setUsers(newUsers);
      });
    return () => unsubscribe();
  }, []);
  return users;
}

function DisplayUsers() {
  const users = useWeights();

  const [currentId, setCurrentId] = useState("");
  const [showModal, setShowModal] = useState(false);

  const deleteUser = (id) => {
    firebase.firestore().collection("users-weight").doc(id).delete();
  };

  return (
    <div>
      <h2>Users</h2>

      <div>
        <table class="max-w-5xl mx-auto table-auto">
          <thead class="justify-between">
            <tr class="bg-green-600">
              <th class="px-16 py-2">
                <span class="text-gray-100 font-semibold">Name</span>
              </th>
              <th class="px-16 py-2">
                <span class="text-gray-100 font-semibold">Weight</span>
              </th>

              <th class="px-16 py-2">
                <span class="text-gray-100 font-semibold"></span>
              </th>
            </tr>
          </thead>
          <tbody class="bg-gray-200">
            <tr class="bg-white border-b-2 border-gray-200">
              <td class="px-16 py-2 flex flex-row items-center">
                <img
                  class="h-8 w-8 rounded-full object-cover "
                  src="https://randomuser.me/api/portraits/men/30.jpg"
                  alt=""
                />
              </td>
              <td>
                <span class="text-center ml-2 font-semibold">John Doe</span>
              </td>

              <td class="px-16 py-2">
                <span>05/06/2020</span>
              </td>
              <td class="px-16 py-2">
                <span>10:00</span>
              </td>

              <td class="px-16 py-2">
                <span class="text-yellow-500 flex"></span>
              </td>
            </tr>
          </tbody>
        </table>
        <ol>
          {users.map((user) => (
            <li key={user.id} className="flex space-x-5">
              {/* <h3>{user.name}</h3>
              <p>{user.weight}</p>
              <p>{user.id}</p> */}
              <div></div>
              <div className="flex items-center">
                <MdDelete
                  className="cursor-pointer"
                  onClick={() => {
                    deleteUser(user.id);
                  }}
                />
                <BiPencil
                  className="cursor-pointer"
                  onClick={() => {
                    setCurrentId(user.id);
                    setShowModal(true);
                  }}
                />
                {showModal && (
                  <Modal
                    currentId={currentId}
                    showModal
                    onClose={() => setShowModal(false)}
                  />
                )}
              </div>
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default DisplayUsers;
