import React from "react";
import firebase from "firebase";

import StyleFirebaseAuth from "react-firebaseui/StyledFirebaseAuth";

var uiConfig = {
  signInFlow: "popup",
  signInOptions: [
    firebase.auth.EmailAuthProvider.PROVIDER_ID,
    firebase.auth.GoogleAuthProvider.PROVIDER_ID,
    firebase.auth.GithubAuthProvider.PROVIDER_ID,
    firebase.auth.PhoneAuthProvider.PROVIDER_ID,
  ],
  callbacks: {
    signInSuccessWithAuthResult: async (authResult) => {
      const userInfo = authResult.additionalUserInfo;
      if (userInfo.isNewUser && userInfo.providerId === "password") {
        try {
          await authResult.user.sendEmailVerification();
          console.log("check your email");
        } catch (e) {
          console.log("Unable to send email verification email");
        }
      }
      return false;
    },
  },
};

function Login(props) {
  const {
    email,
    password,
    setEmail,
    setPassword,
    handleLogin,
    handleSignup,
    hasAccount,
    setHasAccount,
    emailError,
    passwordError,
  } = props;

  return (
    // <div className="min-h-screen bg-gray-50 flex flex-col justify-center">
    //   <label>Username</label>
    //   <input
    //     type="text"
    //     autoFocus
    //     required
    //     className="appearance-none  relative block px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900 rounded-md focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm w-80"
    //     value={email}
    //     onChange={(e) => setEmail(e.target.value)}
    //   />
    //   <p>{emailError}</p>
    //   <label>Password</label>
    //   <input
    //     className="
    //       appearance-none rounded-md relative block px-3 py-2 border border-gray-300 placeholder-gray-500 text-gray-900  focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 focus:z-10 sm:text-sm w-80"
    //     type="password"
    //     required
    //     value={password}
    //     onChange={(e) => setPassword(e.target.value)}
    //   />
    //   <p>{passwordError}</p>

    //   <div>
    //     {hasAccount ? (
    //       <>
    //         <button
    //           className="border rounded-md text-center p-3 w-30 mt-3 flex bg-blue-700 flex-1"
    //           onClick={handleLogin}
    //         >
    //           Sign In
    //         </button>
    //         <p>
    //           Don't have an account ?
    //           <span onClick={() => setHasAccount(!hasAccount)}>Sign Up</span>
    //         </p>
    //       </>
    //     ) : (
    //       <>
    //         <button onClick={handleSignup}>Sign Up</button>
    // <StyleFirebaseAuth
    //   uiConfig={uiConfig}
    //   firebaseAuth={firebase.auth()}
    // />
    //         <p>
    //           Have an account ?
    //           <span onClick={() => setHasAccount(!hasAccount)}>Sign In</span>
    //         </p>
    //       </>
    //     )}
    //   </div>
    // </div>
    <div className=" min-h-screen bg-gray-50 flex flex-col justify-center">
      <div className="max-w-md w-full mx-auto">
        <div className="text-3xl font-bold text-gray-900 mt-2 text-center">
          WEIGHT TRACKER APP
        </div>
      </div>
      <div className="max-w-md w-full mx-auto mt-4 bg-white p-8 border border-gray-300">
        <div action="" className="space-y-6">
          <div>
            <label className="text-sm font-bold text-gray-600 block">
              Email
            </label>
            <input
              type="text"
              autoFocus
              required
              className="w-full p-2 border border-gray-300 rounded mt-1 "
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <p className="font-medium text-sm text-red-500">{emailError}</p>
            <label className="text-sm font-bold text-gray-600 block">
              Password
            </label>
            <input
              type="password"
              required
              className="w-full p-2 border border-gray-300 rounded mt-1"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <p className="font-medium text-sm text-red-500">{passwordError}</p>
          </div>
          <div>
            {hasAccount ? (
              <button
                onClick={handleLogin}
                className="w-full py-2 px-4 bg-blue-600 hover:bg-blue-700 rounded text-white text-sm"
              >
                Sign In
              </button>
            ) : (
              <button
                onClick={handleSignup}
                className="w-full py-2 px-4 bg-blue-600 hover:bg-blue-700 rounded text-white text-sm"
              >
                Sign Up
              </button>
            )}
          </div>
          <div className="border bottom-t-1 border-gray-300"></div>

          <StyleFirebaseAuth
            uiConfig={uiConfig}
            firebaseAuth={firebase.auth()}
          />
        </div>
      </div>
    </div>
  );
}

export default Login;
