import React from "react";
import fire from "../firebase";
import socialAuth from "../fireAuth";

function Login(props) {
  const {
    email,
    password,
    setEmail,
    setPassword,
    handleLogin,
    handleSignup,
    hasAccount,
    setHasAccount,
    emailError,
    passwordError,
  } = props;

  const handleOnClicks = async (provider) => {
    const res = await socialAuth(provider);
    console.log(res);
  };

  var googleProvider = new fire.auth.GoogleAuthProvider();
  return (
    <section className="login">
      <div>
        <label>Username</label>
        <input
          type="text"
          autoFocus
          required
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <p>{emailError}</p>
        <label>Password</label>
        <input
          type="password"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <p>{passwordError}</p>
      </div>
      <div>
        {hasAccount ? (
          <>
            <button onClick={handleLogin}>Sign In</button>
            <button onClick={handleOnClicks(googleProvider)}>Google</button>
            {/* <button onClick={handleOnClicks(githubProvider)}>Github</button> */}
            <p>
              Don't have an account ?
              <span onClick={() => setHasAccount(!hasAccount)}>Sign Up</span>
            </p>
          </>
        ) : (
          <>
            <button onClick={handleSignup}>Sign Up</button>
            <p>
              Have an account ?
              <span onClick={() => setHasAccount(!hasAccount)}>Sign In</span>
            </p>
          </>
        )}
      </div>
    </section>
  );
}

export default Login;
