import React, { useState, useEffect } from "react";
import { firebase, db } from "../firebase";

function AddUserForm() {
  const [name, setName] = useState("");
  const [weight, setWeight] = useState(0);
  db.collection("users-weight")
    .add({
      name: "chirag",
      weight: 80,
    })
    .then((docRef) => {
      console.log("Document written with ID: ", docRef.id);
    })
    .catch((error) => {
      console.error("Error adding document: ", error);
    });

  const onSubmit = (e) => {
    e.preventDefault();
    firebase
      .firestore()
      .collection("users-weight")
      .add({
        name,
        weight,
      })
      .then(() => {
        setName("");
        setWeight(0);
      });
  };

  return (
    <form>
      <div className="mt-10 h-16 grid grid-cols-2 grid-flow-row gap-2 p-4 ">
        <div className="w-96 flex h-12 space-x-2 items-center">
          <label className="text-sm font-bold text-gray-600 block mb-1">
            Name:
          </label>
          <input
            type="text"
            value={name}
            autoFocus
            required
            className="w-50 mb-2 p-2 border border-gray-300 rounded mt-1 "
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="w-96 flex h-12 space-x-2 items-center">
          <label className="text-sm font-bold text-gray-600 block mb-1">
            Weight:
          </label>
          <input
            type="text"
            value={weight}
            required
            className="w-50 mb-2 p-2 border border-gray-300 rounded mt-1 "
            onChange={(e) => setWeight(e.target.value)}
          />
        </div>
      </div>
      <button
        type="button"
        className="py-2 px-4 w-24 flex justify-center items-center  bg-blue-600 hover:bg-blue-700 text-white text-center text-base font-semibold shadow-md rounded-lg mx-auto my-3"
      >
        + Add
      </button>
    </form>
  );
}

export default AddUserForm;
