import React, { useState, useEffect } from "react";
import fire from "../firebase";
import firebase from "firebase";

function Hero({ handleLogout }) {
  const [name, setName] = useState("");
  const [weight, setWeight] = useState(0);
  const [users, setUsers] = useState([]);

  const db = firebase.firestore().collection("users-weight");
  console.log(db);

  function getUsers() {
    db.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
      });
      setUsers(items);
    });
  }

  const addUser = (user) => {
    db.doc(user.uid).set({});
  };

  useEffect(() => {
    getUsers();
  }, []);
  return (
    <>
      <nav className="flex justify-between w-full h-16 items-center bg-blue-600 shadow-lg">
        <h1 className="font-semibold text-xl pl-3">Weight Tracker App</h1>
        <button
          className="h-9 w-20 font-semibold bg-white mr-3 rounded-full hover:bg-gray-50"
          onClick={handleLogout}
        >
          Logout
        </button>
      </nav>
      <div className="mt-10 h-16 grid grid-cols-2 grid-flow-row gap-2 p-4 ">
        <div className="w-96 flex h-12 space-x-2 items-center">
          <label className="text-sm font-bold text-gray-600 block mb-1">
            Name:
          </label>
          <input
            type="text"
            value={name}
            autoFocus
            required
            className="w-50 mb-2 p-2 border border-gray-300 rounded mt-1 "
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div className="w-96 flex h-12 space-x-2 items-center">
          <label className="text-sm font-bold text-gray-600 block mb-1">
            Weight:
          </label>
          <input
            type="text"
            value={weight}
            required
            className="w-50 mb-2 p-2 border border-gray-300 rounded mt-1 "
            onChange={(e) => setWeight(e.target.value)}
          />
        </div>
      </div>
      <button
        type="button"
        className="py-2 px-4 w-24 flex justify-center items-center  bg-blue-600 hover:bg-blue-700 text-white text-center text-base font-semibold shadow-md rounded-lg mx-auto my-3"
      >
        + Add
      </button>
      <div>
        <h1>Users</h1>
        {users.map((user, idx) => (
          <div key={idx}>
            <h2>{user.Name}</h2>
            <p>{user.weight}</p>
          </div>
        ))}
      </div>
    </>
  );
}

export default Hero;
