import React from "react";
import firebase from "firebase";
import socialAuth from "../fireAuth";

function Login(props) {
  const {
    email,
    password,
    setEmail,
    setPassword,
    handleLogin,
    handleSignup,
    hasAccount,
    setHasAccount,
    emailError,
    passwordError,
  } = props;

  const handleOnClicks = async (provider) => {
    const res = await socialAuth(provider);
    console.log(res);
  };

  var googleProvider = new firebase.auth.GoogleAuthProvider();
  var githubProvider = new firebase.auth.GithubAuthProvider();
  return (
    <section className="login">
      <div>
        <label>Username</label>
        <input
          type="text"
          autoFocus
          required
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <p>{emailError}</p>
        <label>Password</label>
        <input
          type="password"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <p>{passwordError}</p>
      </div>
      <div>
        {hasAccount ? (
          <>
            <button onClick={handleLogin}>Sign In</button>
            <button onClick={handleOnClicks(googleProvider)}>Google</button>
            <button onClick={handleOnClicks(githubProvider)}>Github</button>
            <p>
              Don't have an account ?
              <span onClick={() => setHasAccount(!hasAccount)}>Sign Up</span>
            </p>
          </>
        ) : (
          <>
            <button onClick={handleSignup}>Sign Up</button>
            <button
              onClick={handleOnClicks(googleProvider)}
              type="button"
              class="py-2 px-4 flex justify-center items-center  bg-red-600 hover:bg-red-700 focus:ring-red-500 focus:ring-offset-red-200 text-white w-full transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none focus:ring-2 focus:ring-offset-2  rounded-lg "
            >
              <svg
                width="20"
                height="20"
                fill="currentColor"
                class="mr-2"
                viewBox="0 0 1792 1792"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path d="M896 786h725q12 67 12 128 0 217-91 387.5t-259.5 266.5-386.5 96q-157 0-299-60.5t-245-163.5-163.5-245-60.5-299 60.5-299 163.5-245 245-163.5 299-60.5q300 0 515 201l-209 201q-123-119-306-119-129 0-238.5 65t-173.5 176.5-64 243.5 64 243.5 173.5 176.5 238.5 65q87 0 160-24t120-60 82-82 51.5-87 22.5-78h-436v-264z"></path>
              </svg>
              Sign in with Google
            </button>
            <button onClick={handleOnClicks(githubProvider)}>Github</button>
            <p>
              Have an account ?
              <span onClick={() => setHasAccount(!hasAccount)}>Sign In</span>
            </p>
          </>
        )}
      </div>
    </section>
  );
}

export default Login;
