import React, { useEffect, useState } from "react";
import { firebase } from "../firebase";
import { MdDelete } from "react-icons/md";
import UpdateUser from "./UpdateUser";

function useWeights() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    const unsubscribe = firebase
      .firestore()
      .collection("users-weight")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshot) => {
        const newUsers = snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));
        setUsers(newUsers);
      });
    return () => unsubscribe();
  }, []);
  return users;
}

function DisplayUsers({ showModal, setShowModal }) {
  const users = useWeights();

  const initialItemState = [{ id: null, name: "", weight: "" }];
  const [currentItem, setCurrentItem] = useState(initialItemState);
  const editItem = (item) => {
    setCurrentItem({
      id: item.id,
      name: item.name,
      weight: item.weight,
    });
  };

  const updateItem = ({ currentItem }, updatedItem) => {
    console.log(
      "It send the item to the updated item function:",
      updatedItem,
      currentItem.id
    );
    firebase
      .firestore()
      .collection("users-weight")
      .doc(currentItem.id)
      .update(updatedItem);
  };

  const deleteUser = (id) => {
    firebase.firestore().collection("users-weight").doc(id).delete();
  };

  return (
    <div>
      <h2>Users</h2>
      <div>
        <ol>
          {users.map((user) => (
            <li key={user.id} className="flex justify-between">
              <h3>{user.name}</h3>
              <p>{user.weight}</p>
              <p>{user.id}</p>
              <MdDelete onClick={() => deleteUser(user.id)} />
              <UpdateUser
                onChange={() => {
                  editItem(user);
                  console.log(user);
                }}
                showModal={showModal}
                setShowModal={setShowModal}
                currentItem={currentItem}
                updateItem={updateItem}
              />
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default DisplayUsers;
